css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cat-v.org") {
  #main-copy, #side-bar, #side-bar a, body{
    background-color: #333;
  }
  #main-copy {
    color: #ddd;
    border-left: 1px solid #222;
  }
  #side-bar{
    border-right: 1px solid #222;
  }
  #side-bar a{
    border-left: 0.2em solid #222;
  }
  #side-bar div {
    border-bottom: 1px solid #222;
  }
  #main-copy h1, #main-copy h2, #main-copy a, #side-bar a {
    color: #5391CF;
  }
  body{
    color: #ddd;
  }
  .thisPage, .thisPage a{
    color: #ddd !important;
  }
  td{
    background-color: #314960;
  }
  th{
    background-color: #628CB6;
  }
}
