css:
@-moz-document url("https://tweetdeck.twitter.com/"), url("https://tweetdeck.twitter.com/#"), url-prefix("https://twitter.com/") {
.prf-stats {
    visibility: hidden;
}

.DashboardProfileCard-stats {
    visibility: hidden;
}

.ProfileNav-list {
    visibility: hidden;
}

.ProfileNav-item--userActions {
    visibility: visible;
}

.profile-banner-footer {
    visibility: hidden;
}

.profile-picture {
    visibility: visible;
}
}
