css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.rap-n-blues.com") {
    @media screen and (max-width: 770px) {
        .tablepress {
            width: auto;
        }
        thead,
        tbody,
        tr,
        td,
        th {
            box-sizing: border-box;
            display: block;
        }
        tr:after {
            content: ' ';
            display: block;
            visibility: hidden;
            clear: both;
        }
        /*any os other than mac*/
        
        tbody {
            overflow-y: auto;
        }
    }
}
