css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.zhibo8.cc"), domain("zhibo8.cc") {

  /*去广告*/
  /*******************************************/
  #popautoapp {
    display: none !important;
  }
  /*******************************************/
  
  
  /*去篮球部分*/
  /*******************************************/
  #recommend > div:nth-child(n+3):nth-child(-n+5){
    display: none !important;
  }
  .bbs_container > div:nth-child(2) {
    display: none !important;
  }
  /*******************************************/

  
  /*宽度调整*/
  /*******************************************/
  .container {
    width: 1120px !important;
  }
  #recommend {
    width: 920px !important;
  }
  #recommend .r_video, #recommend .r_news {
    width: 450px !important;
  }
  .schedule_container {
    width: 880px !important;
  }
  .bbs_container {
    width: 220px !important;
  }
  /*******************************************/
  
  
  /*字体调整*/
  /*******************************************/
  #recommend .head, #recommend li {
    font-size: 16px !important;
    line-height: 30px !important;
  }
  .schedule_container .box .content ul li {
    font-size: 16px !important;
  }
  .bbs_container .box .content ul li {
    font-size: 16px !important;
    line-height: 34px !important;
  }

  /*******************************************/
}
