css:
@-moz-document domain("g2a.com") {
/* Removing the body clicker */
body {
  background-color: rgb(30, 31, 32) !important;
}

#body_clicker {
  display: none !important;
}

#container2 {
  padding: 3% !important;
}

/* Header annoyances */
.mp-head-goldmine, .mp-head-stc, .mp-head-giftcard-center {
  display: none !important;
}
  
.mp-head-giftcard.active {
  height: 0px !important;
}
  
/* We don't need the footer */
#footer-wrap #footer3, #footer-wrap, #content-tabs > img {
 display: none !important;
}

.widget-tp-reviews {
	display: none !important;
}

/* Annoying and misleading */
.product_f-right {
    display: none !important;
}

.product_f-left {
    margin-left: 135px;
    margin-right: 150px;
}

.product_f {
    background: rgb(243, 243, 243);
}

/* More self-promotion annoyances */
.banner-shield, .banner-shield-small, #supportHubToolTip {
	display: none !important;
}
}
