css:
@-moz-document domain(intranet.teleopti.com) {

  body, #header, #navigation {
      background: #F7F793;
      font-family:"Comic Sans MS" !important;
  }

  #content-inner {
   width:98%;
  }

  #navigation #primarynav ul {
  	background: none;
  }
  
  .landing .slideshow .ig-widget-html {
  	display: none;
  }

  #content, #content-body, body#saml {
  background-image: -webkit-gradient(
	linear,
	left bottom,
	left top,
	color-stop(0, #FFB2F1),
	color-stop(0.65, #F7F793),
	color-stop(1, #FFD6FF)
);
background-image: -o-linear-gradient(top, #FFB2F1 0%, #F7F793 65%, #FFD6FF 100%);
background-image: -moz-linear-gradient(top, #FFB2F1 0%, #F7F793 65%, #FFD6FF 100%);
background-image: -webkit-linear-gradient(top, #FFB2F1 0%, #F7F793 65%, #FFD6FF 100%);
background-image: -ms-linear-gradient(top, #FFB2F1 0%, #F7F793 65%, #FFD6FF 100%);
background-image: linear-gradient(to top, #FFB2F1 0%, #F7F793 65%, #FFD6FF 100%);
  }

  h2.ig-cpt-title, h2.ig-cpt-title a, #navigation #primarynav li.here a, #navigation #primarynav a:hover, #navigation a:focus, #userbar, #footer {
      background-color: lightpink !important;
  }

  #navigation #primarynav a, h2.ig-cpt-title, h2.ig-cpt-title a, .ig-arrow {
      border-bottom: 3px solid lightpink;
      font-family: "Comic Sans MS";
  }

  #userbar li a:hover {
      background: steelblue;
  }

  #userbar ul li {
      border: #eaeaea solid;
  }

  .ig-user-thumbnail, .thumbnail {
      border-radius: 50px;
  }
  
  h2.ig-cpt-title, h2.ig-cpt-title a {
  	line-height: 20px;
  }
}
