css:
@-moz-document url-prefix("https://www.linkedin.com") {
[role="main"] {
	width: auto !important;
	padding: 1em;
}

.top-bar {
	width: auto !important;
}
.card-container {
	margin-right: 0 !important;
}

.wrapper {
	width: auto !important;
	padding: 0 1em;
}

#left-container {
	flex: 1;
}

#content {
	float: none;
	width: auto;
}
#main {
	display: flex;
	width: auto;
}

#identity {
	float: none;
	width: auto;
	display: flex;
}

#identity .member {
	flex: 1;
}

#ozfeed {
	width: auto;
	float: none;
}

/* Sidebar */

#props {
	height: calc(100vh - 16em) !important;
	overflow-y: scroll;
}

#props .card {
	opacity: inherit;
	width: inherit;
	height: inherit;
	margin-bottom: 1em;
	display: block !important;
	white-space: initial;
}

#props .card.current {
	width: auto;
}

#props .card.current+.card ~ .card {
	opacity: inherit;
	width: inherit;
}

/* Jobs search */

.jobs-home {
	display: flex;
	flex-wrap: wrap;
}

.search-module {
	width: 100%;
}
}
