css:
@-moz-document domain(football.fantasysports.yahoo.com) {

html {
 color:#DADADA;   
}

.Table th {
 color: #B0B0B0;   
}


a {
 color: #8EC0FF;   
}

.Nav-h:not(.Nav-main) > .Navitem.Selected > .Navtarget
{
 color: #EEEEEE;   
}

.Nav-main > .Selected > .Navtarget, .Nav-main > .Selected:hover > .Navtarget
{
   color: #EEEEEE;    
}


.Mod {
      background-color:#161719;  
}



#Stencil .Bdr, #Stencil .Bdrx, #Stencil .Bdry, #Stencil .Bdrtop, #Stencil .Bdrbot, #Stencil .Bdrstart, #Stencil .Bdrend
{
  border-color:#232439;   
}

.Table td {
    border-color:#232439;   
}

.Table-interactive tr:hover > td {
 background-color:#161719!important;
}

.Table-interactive tr:hover > td.Selected {
    background-color:#464852!important;
}

.Table td.Selected {
    background-color:#464852!important;
}
.Table th.Selected
{
    background-color:#464852!important;
}
.Thm-inherit .ysf-rosterswapper tbody tr.bench
{
 background-color:#1C1D1F!important;  
}

.Nav-h:not(.Nav-main) {
 border-bottom-color:#232439!important;  
 border-top-color:#232439!important;  
}


.Page-maincontent, .Page-footercontent, .Page-ft
{
  background-color:#232429;  
}

.template-html5 .Subnav-main-wrap:before, .template-html5 .Subnav-main-wrap:after, .template-html5 .Page-bd:before, .template-html5 .Page-bd:after, .template-html5 .Page-ft:before, .template-html5 .Page-ft:after
{
  background-color:#232429;  
}
.template-html5 .Subnav-main-wrap, .template-html5 .Page-bd, .template-html5 .Page-ft
{
    background-color:#232429;    
}

.Subnav-main, #Stencil .Bg-shade2
{
 background-color:#2E2F30!important;   
}

.Table thead > tr:not(.First), .Table thead > tr.First.Last
{
     background-color:#0F1011;
}


.Btn:hover, .Btn-secondary:hover
{
    background-color: #000;
}
 .Btn, .Btn-secondary {
    background-color: #0078FF;
    border-color: #0078FF;
    color: #FFF;      
}


/* Modal */

.yui3-ysplayernote .yui3-widget-ft
{
  background-color: #333;
}

.yui3-ysplayernote .playernoteview-notes
{
  background-color: #333;
}

.yui3-ysplayernote .teamsched-bd, .yui3-ysplayernote .yui-dt-bd
{
  background-color: #333;
}
.yui3-ysplayernote .teamsched
{
  background-color: #333;
}

.yui3-ysplayernote .teamsched-bd h5
{
  background-color: #333;
}

.teamsched-bd .even
{
  background-color: #333;
}

.yui3-ysplayernote .teamsched-ft p, .yui3-ysplayernote .playernotes-ft p
{
  background-color: #333;
}

#yspfooter {
  background:#333;
}


.yspsubcontent #yspmain, .yspcontent #yspmain {
  background-color:#333;
}

#bd { 
    background-color: #333;
}

#yspmessage .bd .thread-topic,
#yspmessage .bd .thread-message,
#ysparchive_message #yspmain .thread-topic,
#ysparchive_message #yspmain .thread-message{
	border:1px solid #555;	
	background-color:#444;
}

#yspmessage .bd .thread-message,
#ysparchive_message #yspmain .thread-message{
	background-color:#333;
}

.yspsubcontent #yspsub, .yspcontent #yspsub {
  background-color: #333;
}


}
