css:
@-moz-document domain("theaquila.net") {
a:link, a:visited, .forum_on {
    color: #DEAD00
}

.thead {
background: #4c4c4c; /* Old browsers */
background: linear-gradient(135deg, #4c4c4c 0%,#595959 12%,#666666 25%,#474747 39%,#2c2c2c 50%,#000000 51%,#111111 60%,#2b2b2b 76%,#1c1c1c 91%,#131313 100%); /* W3C */
}

#content, #logo, #header, #panel .lower {
    background: transparent;
}

#container {
    /*background: url(http://1-background.com/images/black-dreams/black-dreams-in-blue-website-background.jpg);*/
    background: url(https://nf-14a3.kxcdn.com/public/style_images/images/bg_mosaic.png);
}

#logo, #panel .upper {
    border-bottom: none;
}

#logo .wrapper a:first-child img {
    content: url(http://i.imgur.com/BiQuTRB.png);
    /*content: url(http://i.imgur.com/6Od2Elz.png);*/
}

#search input.button {
    background: #222;
}
#search input.button:hover {
    background: #333;
}

.trow_sep {
    background: #222;
    color: #DDD;
    border-bottom-color: #dead00;
}

.post_content {
    background: rgba(20, 20, 20, 0.5);
}

.ui-helper-hidden-accessible {
    display: none;
}

.trow1, .trow2 {
    background-color: rgba(20,20,20,0.7);
}

.tab-links a{
    background: #222;
}
.tab-links a:hover{
    background: #555;
}
}
