css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://www.youtube.com/feed/subscriptions") {
  #browse-items-primary > ol.section-list {
    list-style-type: bullet;
  }
  
  #browse-items-primary > ol.section-list > li {
    display: inline-block;
    width: 19em;
    height: 19em;
    vertical-align: top;
    padding-top: 1em;
  }
  
  #browse-items-primary > ol > li > ol > li > div > div > div > ul > li > div > div > .yt-lockup-dismissable > .yt-lockup-content {
    display: inline-block;
  }
  .yt-lockup-byline {
    display: none;
    width: 0px;
  }
}
