css:
@-moz-document url("https://www.youtube.com/"){

  #page-container, #masthead-appbar, #footer, #footer-container, .skip-nav, #appbar-guide-button-container, #yt-masthead-user, .content-region{
    /**/display: none;/**/
  }

  #masthead-positioner {
    top: 40%;
  }
  .search-form.consolidated-form{
    display: block;
    margin: 0 auto;
    width: 100%;
  }

  #yt-masthead .yt-masthead-logo-container #logo-container {
    margin: 0 auto;
    width: 100%;
  }

  #yt-masthead .yt-masthead-logo-container {
    float: none;
    left: calc(50% - 72px);
  }

}
