css:
@-moz-document domain("www.gsmarena.com") {
.float-right, .right {
    float:left !important;
}
.sidebar.col.left {
    float: right !important;
    opacity: 0.2 !important;
    padding: 0 0 0 20px !important;
	transition: opacity 0.5s !important;
}
.sidebar.col.left:hover {
    opacity: 0.7 !important;
	transition: opacity 0.5s !important;
}
#subHeader {
    display:none !important;
}
.news-column-index {
	width:460px !important;
	padding-left:10px !important;
}
.news-column-list-item {
	margin-bottom:40px !important;
}
.news-column-list-item h3, .postTitleLink {
	font-weight:400 !important;
	color:#b23f33 !important;
}
/*#outer.row {
	margin-top:10px !important;
}*/
.blog-column-list-item h3 {
	font-weight:400 !important;
	font-size:14px !important;
	margin-bottom:10px !important;
}
.main-menu-list {
	display:block !important;
	border: none !important;
	background: #e0ddd6  !important;
}
.main-menu-list li a {
	border: none !important;
}
#header, .row.slide.headroom--top, #header::after {
	height:97px !important;
	min-height:97px !important;
	border: none !important;
}
#header:after {
	content:" " !important;
	display:block !important;
	clear: both !important;
	float:none !important;
}
#header+#wrapper,
#header~#wrapper {
    padding-top: 98px
}
}
