css:
/* STYLE BY UNHOLYMASTER || TUMBLR & INSTAGRAM */

@-moz-document domain("spellsofmagic.com") {

.w, element.style, .body, body {
background-color: #000000;
color: #4f4f4f;
}

.tl, .l, .fp {
background: #191919;
}

.tr {
background: #191919;
border-top: #191919;
}

.mm, .mm2, .sm, .sm2 {
color: #4f4f4f;
background: #212121;
border-top: 2px solid #191919;
border-left: 2px solid #191919;
border-right: 2px solid #191919;
}

.h, .hr, .hrl, .hrr {
background: #212121;
border: 2px solid #191919;
}

.i {
background-image: url('http://i61.tinypic.com/10nvv5e.jpg');
}

#sl1, #sl2, #sl3, #sl2p, #sl3p2, #sl3p, body, td, h1, .hrt, a:hover {
color: #4f4f4f;
}

a, .mobile {
color: #d8d8d8;
}

input, center, body, td, html {
background: #000000;
}

.cal {
background: #212121;
}

.cal2, .cal3 {
background: #d9d9d9;
}

.lbar {
display: none !important;
}

.fcx, .fcx2 {
border-left: 1px solid #212121;
}

.fi {
color: #212121;
}

input {
background: #212121;
color: #d9d9d9;
}

.header_bar2 {
border: solid 1px #191919;
color: #4f4f4f;
background: #212121;
padding: 2px;
}

.mm:hover {
background: #d9d9d9;
color: #4f4f4f;
border-top: 2px solid #4f4f4f;
border-left: 2px solid #4f4f4f;
border-right: 2px solid #4f4f4f;
}

.sm:hover, .sm2:hover {
background: #4f4f4f;
color: #d9d9d9;
border-top: 2px solid #d9d9d9;
border-left: 2px solid #d9d9d9;
border-right: 2px solid #d9d9d9;
}

.mm2:hover {
background: #d9d9d9;
color: #4f4f4f;
border-top: 2px solid #4f4f4f;
border-left: 2px solid #4f4f4f;
border-right: 2px solid #4f4f4f;
}

.dash {
border-bottom: 1px dashed #212121;
border-right: 1px dashed #212121;
}

.ft, .fi:hover {
color: #d9d9d9;
}

.mp {
background-image:url('http://i58.tinypic.com/2lt4ymp.jpg');
}

element.style {
border: 1px solid #212121;
}

}
