css:
@-moz-document domain("web.telegram.org"), domain("twitter.com") {
* {font-family:'proxima nova','helvetica','noto sans cjk sc demilight','Frutiger LT Arabic','Noto Sans Devanagari',Arial,sans-serif,Segoe UI Symbol,HanaMinA, HanaMinB;font-weight:400;}
.is-wide-columns .column {width: 33.2%;min-width:245px;margin-right: 1px;}
.needs-scroll-bottom-offset.scroll-styled-h {bottom:0;}
.app-columns {padding:0}
.compose-text {height:110px!important;}
.js-column-header,.tweet-footer,.antiscroll-inner,.antiscroll-inner > *:not(.compose-text-container) {-webkit-user-select:none;}
.trends-column {display:none;}
/*colors*/
.btn-on-blue:hover {background-color:hsl(206,78%,45%)!important;}
a,.compose-reply-tweet .tweet-body a,.facet-subtitle,.toggle-item {color:hsl(162,78%,60%)}
a:hover,a:active,a:focus {color:hsl(162,78%,45%)}
.btn,.spinner-button-icon-spinner,.icon-sliders,.is-new .column-type-icon,.btn-on-blue:hover,.more-tweets-glow {-webkit-filter:hue-rotate(316deg);}
/*to calculate angle use HSL color. angle=154+H (base=206)*/

/*grays*/
.app-content,.compose  {background-color:#111}
.bg-r-white,textarea,.compose-text-container {background:#fcfcfc}
.replyto-caret {border-color: transparent transparent #fcfcfc transparent;}
.facet-type.is-expanded,.med-fullpanel,.is-selected-tweet,.column-detail .is-selected-tweet {background:rgba(0,0,0,0.4)}
.is-options-open .column-settings-link,.column-options,.mdl-media-prev, .mdl-media-next,.column,.js-column-header,.app-columns-container,*[class*="js-tweet"]:not(.js-tweets-from),.js-column-detail * {border-left:none;border-right:none;background-color:transparent}
::selection {background:#ccc;color:#fcfcfc}
}
