css:
@-moz-document domain("vimeo.com") {

  /* videos - browsing */
  #browse_content a:visited {
    color: #800080 !important;
  }

  /* videos - searching */
  .clip_thumbnail a:visited span {
    color: #800080 !important;
  }
  
  /*recently uploaded videos*/
  #recent_videos a:visited h3 {
    color: #800080 !important;
  }
}
