css:
@-moz-document url-prefix("https://atom.io/docs/api") {
/* Always expand documentation blocks */
.documents .api-entry {
  padding-bottom: 10px;
}

.documents .api-entry .method-summary-wrapper {
  display: block;
  padding-bottom: 20px;

  /* Indent doc blocks to make names stand out */
  padding-left: 20px;
}

/* Use bolder, more visible names */
.documents .api-entry > .name {
  border-left: 2px solid #40a977;
  font-size: 16px;
  font-weight: bold;
  padding-left: 6px;
}

/* Always expand extended methods */
.documents .extended-methods-container .extended-methods {
  display: block;
}
}
