css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mail.google.com") {
/* Hide top ad bar */
/* @todo: No longer necessary? */
div.nH div.mq {
	display: none !important;
}

/* Hide side ad bar */
div.nH.HbF0O,
.Bu + .Bu {
	display: none !important;
}

/* Hide bottom ad bar */
/* @todo: No longer necessary? */
div.mq.nH.oy8Mbf,
div.OH0apf {
	display: none !important;
}

/* Reduce empty space at edge of page */
td.Bu > .nH.if,
.nH.hx .nH .Bk .gA .gB .ip.iq {
	margin-right: 0 !important;
}
td.Bu > .nH.if .nH.aHU .nH.hx {
	padding-right: 0 !important;
}

/* Reduce empty space at bottom of page */
div.l2.ov {
	padding-bottom: 3em !important;
}

/* Fix font style on storage usage and account activity info when viewing mail */
/* @todo: Doesn't seem to affect Details link??? */
div.l2 .aeV .md.mj,
div.l2 .aeV .md.mj .l8,
div.l2 .ae3 .l6,
div.l3 .ae3 .l6 > .l8 {
	color:       rgb(102,102,102) !important;
	font-weight: normal !important;
}

/* Hide 'Powered by Google' bit */
div.l2 div > div > div.ma {
	visibility: hidden !important;
}

/* Remove unnecessary padding on account activity info */
div.l2 .ae3 .l6 {
	padding-top: 0;
}

/* Hide 'Get Marketplace Apps' bit */
/* @todo: No longer necessary? */
.nH.oy8Mbf .GcwpPb-MEmzyf {
	display: none !important;
}

/* Hide 'what's new' bit */
/* @todo: No longer necessary? */
div.nH > div.nH > div.nH > div.iiDCmd,
div.nH.g.id div.nH div.AT {
	display: none !important;
}

} /* eof */
