css:
@-moz-document url-prefix(http://steamcn.com/) {
    #nav-menu {
        min-width: 970px;
    }
    
    #nav-menu > ul > li {
        margin-right: unset;
    }
    
    .tb-container {
        width: calc(100% - 75px);
    }
    
    .index_navi {
        margin: 12px 20px 18px;
        width: unset;
    }
    
    .index_navi_mid {
        display: none;
    }
    
    .slideshow > li {
        
    }
    
    .wp {
        max-width: calc(100% - 30px) !important;
        min-width: calc(100% - 30px) !important;
    }
    
    #wp.wp.cl {
        min-width: calc(100% - 30px) !important;
        max-width: calc(100% - 30px) !important;
    }
    
    div[class^="index_subject"], .index_middle_subject, .index_foot_subject, .subforunm_foot_intro {
        width: 100%;
    }
    
    .subject_row_detail_text, .middle_subject_detail_text, .foot_subject_detail_text {
        font-size: 13px;
    }
    
    .subject_row_detail_text_down {
        font-size: 11px;
    }
    
    .index_middle_subject > div {
        width: calc(100% / 3.17);
    }
    
    .foot_subject_left {
        width: 204px;
    }
    
    .middle_subject_detail_right {
        display: none;
    }
    
    .foot_subject_right {
        width: calc(100% - 226px);
    }
    
    .foot_subject_detail_right {
        display: none;
    }
    
    .subforunm_foot_intro_left {
        width: calc(100% - 700px);
    }
}

@-moz-document url-prefix(http://steamcn.com/t), url-prefix(http://steamcn.com/forum.php?mod=viewthread), url-prefix(http://steamcn.com/f), url-prefix(http://steamcn.com/forum.php?mod=forumdisplay) {
    .subforum {
        width: 100%;
    }
    
    .subforum_left_title {
        width: calc(100% - 270px);
    }
    
    .subforum_right_title {
        margin-left: calc(100% - 260px);
    }
    
    td[id^="postmessage_"] img[id^="aimg_"] {
        max-width: 100%;
    }
    
    div[class^="subforum_left_title_right"] {
        display: none;
    }
    
    
    .tl .th .num, .tl .num {
        width: 50px;
    }
    
    .tl .by-author {
        width: 125px;
    }
    
    .tl .th .by, .tl .by {
        width: 125px;
    }
    
}
