css:
@-moz-document domain(hromadske.tv) {

body {overflow-x: hidden}

header .head_line .donate_button {
    background-color: transparent;
    height: 71px;
    color: #ef4223;
    background-image: none;
    border-left: 1px solid #c4c4c4;
    border-right: 1px solid #c4c4c4;
}

header .head_line .donate_button:hover {
        background-color: #ef4223;
    color: #fff;
    background-image: none
}

.video-play-list {
  padding-top: 0;
  position: absolute;
  right: 0;
  padding-right: 0;
}
.twitter-timeline {
    width: 277px!important;
      height: 499px;
}

.timeline {
	border-radius: 0!important
}

.playlist-block .btn-opener {
    background-position: 100% -1580px
}

.playlist-block.active .btn-opener {
	background-position: 100% -1716px;
}

.social-row a[href='http://www.dell.ua/'] {
	right: auto!important;
    opacity: 0.5
}

.social-row a[href='http://www.dell.ua/']:hover {
    opacity: 1
}

#footer {padding-left: 0}
.footer-nav-holder  {float: right;}
}
