css:
@-moz-document domain("theverge.com") {
.article-body, .stream-summary, .c-contain {
width: 90% !important;
}
article.permalink .story-image img {
width: 100% !important;
height: 100% !important;
}
.p-dynamic-image,
.l-container > .l-page > .m-article.m-entry > .m-entry__body > .m-entry__sidebar,.l-container > .l-page > .m-article.m-entry > .p-entry-header,
.tags,.social-media-column, .vert300, .social-buttons,
 .btf-leaderboard, #leaderboard_ad, #network-bar,
 .column.grid_4.c-contain.border-l2.rightcol,
 .follow-the-verge.instapaper_ignore.entry-unrelated, .social-col,
 .story-navigation.row.clearfix, .comments, #modal_container, .vox-msg,
 #control_overlay,.p-entry-header *,.p-entry-nav *,.m-footer,.m-article__comments-section *,.m-linkset *,header *,.p-entry-nav *,.js-side-menu *,.m-nav__menu *,#chorus-social-fixed-threshold,.m-article__share-buttons,.m-entry__sidebar *{display:none!important;width:0!important;max-width:0!important;min-width:0!important}
.m-entry__content{width:100%!important}
/*.p-dynamic-image{width:50%!important;margin: 0 auto 0 auto!important}*/
.l-page{border-width:1px!important;margin:0px!important}
}
