css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://codetree.com/projects/vzCL/board?focused") {
  [data-page="boards/show"] .board-container {
    position: fixed;
    top: 0;
    left: 0;
    z-index: 999999999;
    width: 100%;
    height: 100%;
    padding-left: 10%;
  }
  
  [data-page="boards/show"] .board-container .board-column:last-child,
  [data-page="boards/show"] .board-container .board-column:first-child,
  [data-page="boards/show"] .board-container .board-column.hide + .board-column,
  [data-page="boards/show"] .board-column-description {
    display: none;
  }
}
