css:
@-moz-document domain("np.reddit.com") {
a[onclick*='reply(this)'],
form .usertext-edit {
    display: none !important;
}

.link .arrow.up,
.link .arrow.down,
.link .arrow.upmod,
.link .arrow.downmod,
.comment .arrow.up,
.comment .arrow.down,
.comment .arrow.upmod,
.comment .arrow.downmod {
    visibility: hidden !important;
}
}
