css:
@-moz-document url(http://googlefontlab.herokuapp.com/) {

    html, body{
        padding: 0;
        margin: 0;
        width: 100%;
        height: 100%;
        top: 0;
        background: #f6f6f6;
    }

    p, h1, h2, h3, h4, h5, span, input, textarea{
        font-family: "Roboto";
        font-weight: 300;
    }

    header{
        margin: 0 0 30px 0;
        padding: 30px 0;
        height: auto;
        width: 100%;
        background: rgb(66, 133, 244);
        color: #fff;
    }

    header h1, header p{
        width: 100%;
        max-width: 1040px;
        margin: auto;
        text-align: left;
        text-decoration: none;
    }

    header h1{
        margin-top: 40px;
    }

    header p{
        font-size: 1em;
        margin-top: 10px;
    }

    hr{
        display: none;
    }

    .inputsLeft ul{
        margin: 0 0 10px 0;
        border: 0;
        height: 180px;
    }

    .inputsLeft ul li{
        padding: 2px 0px;
        text-align: center;
    }

    .inputsLeft, .inputsRight, .sampleText{
        border: 0;
        box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 
                    0 2px 10px 0 rgba(0, 0, 0, 0.12);
    }

    .inputsRight .optionsHeader span{
        margin: 0;
    }

    .optionsHeader{
        width: 100%;
        background: rgb(66, 133, 244);
        padding: 10px 0;
        margin: 0 0 10px 0;
        color: #fff;
    }

    .sampleText{
        padding: 0 30px 0 30px;
        margin-left: 240px;
        width: 500px;
    }

    @media screen and (max-width: 1040px){
        header h1, header p {
            width: 540px;
        }
        .sampleText{
            top: 520px !important;
            width: 480px;
        }
        .inputsLeft, .inputsRight{
            width: 260px !important;
        }
    }
}
