@-moz-document domain("forums.bohemia.net") {
#ipsLayout_contentArea,
#ipsLayout_mainArea {
	background-color: #2b2b2b;
}
.ipsWidget.ipsWidget_horizontal .ipsWidget_title,
.ipsBox:not( .ipsBox_transparent):not( .ipsModerated) {
	background-color: #2b2b2b;
	color: #ffffff;
}
.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected):not( .ipsModerated):not( .ipsDataItem_new):not( .ipsDataItem_success):not( .ipsDataItem_warning):not( .ipsDataItem_error):not( .ipsDataItem_info):not( .ipsDataItem_status):nth-child(even) {
	background-color: #2f2f2f;
}
.ipsType_richText {
	color: #ffffff;
}
.ipsType_pageTitle {
	color: #ffffff;
}
.ipsNavBar_primary > ul > li.ipsNavBar_active > a {
	background: #333333;
	color: #f5f5f5;
}
.ipsNavBar_primary > ul > li > a {
	background: #f5f5f5;
	color: #333333;
}
.ipsNavBar_secondary {
	background: #f5f5f5;
}
.ipsNavBar_secondary > li.ipsNavBar_active a {
	color: #f5f5f5;
}
.ipsNavBar_secondary {
	background: #2b2b2b;
}
.ipsNavBar_secondary > li > a {
	color: #e6e6e6;
}
#ipsLayout_contentWrapper {
	background: #2b2b2b;
}
.ipsBreadcrumb.ipsBreadcrumb_top {
	color: #ffffff !important;
}
.cke_wysiwyg_frame,
.cke_wysiwyg_div,
.ipsComposeArea_dummy,
.ipsAreaBackground,
.ipsLikeRep,
.ipsSpoiler_header,
.ipsBox:not( .ipsBox_transparent):not( .ipsModerated),
[data-role="commentFeed"] .ipsComment .ipsComment_author,
.ipsAreaBackground_reset,
.ipsSpoiler,
.ipsStyle_spoiler,
.ipsType_richText,
.cke_wysiwyg_frame,
.cke_wysiwyg_div,
.ipsComposeArea_dummy,
.ipsAreaBackground,
.ipsLikeRep,
.ipsSpoiler_header,
.ipsBox:not( .ipsBox_transparent):not( .ipsModerated),
[data-role="commentFeed"] .ipsComment .ipsComment_author,
.ipsAreaBackground_reset,
.ipsSpoiler,
.ipsStyle_spoiler,
.ipsComposeArea_editor,
.ipsType_richText {
	background: #2b2b2b !important;
	color: #ffffff !important;
}
.ipsAreaBackground_light,
.ipsComposeArea_editor,
body {
	background-color: #101010 !important;
	color: #f5f5f5 !important;
}
}

