@-moz-document domain("x.sankuai.com") {
/* 调侃一下 */
img[src*="a49623d596a8"] {
    -webkit-filter: blur(3px);
}

/* 个人恩怨 */
img[src*="lAAAOhEcIi6A8825663"] {
    -webkit-filter: blur(3px);
}

/* 无头像 */
img[src*="default_head"] {
    -webkit-filter:grayscale(1);
}

/* 美女 */
img[src*="a656f683460f"], img[src*="b52753c673f1"], img[src*="CkANwlRe5PqAAHxqAAUFGJJDmXY4742610"], img[src*="309b426f891a"], img[src*="6b8fe1fe6473"], img[src*="658071e8e294"] {
    -webkit-filter:sepia(1);
}

/* 广告 - 封ID */
div[class*="huangjue"] img, div[class*="171259"] img {
    -webkit-filter:brightness(0);
}


/* 忍无可忍 */
img[src*="1763509bfd8f"] {
    -webkit-filter:brightness(0);
}

/* hack 查看群成员 */
