css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.pokebip.com/pokemon/fora") {
  /*Bannière*/
  div.titre
    {background: url(http://i.imgur.com/tbyH4gL.png) no-repeat, #3c3c3c;
    height: 200px;
    width: 1200px}
  
  div.titre img
    {visibility: hidden;}
  
  /*Largeur de page*/
  div.page
    {width: 1200px}
  
  div.colonnes-forum
    {width: 1200px}
  
  div.barre
    {width: 1200px}
  
  /*Posts*/
  .postprofile
    {max-width: 128px;
    text-align: left}
  
  .postbody
    {padding-left: 50px;
    width: 950px;
    float: left;}
  
  /*Fond*/
  body
    {background: url(http://i.imgur.com/peyWyns.png) fixed, url(http://i.imgur.com/CWwqJyF.jpg) fixed}
  
  /*Version mobile*/
  span.lien-skin
   {display: none}
  
  /*Date car "ça sert à rien" (cf bastou)*/
  p.responsive-center.time
  {display: none}
}
