css:
@-moz-document regexp(".*tieba.baidu.com/(f\\?.*w=)?chrome(&.*)?.*") {
.card_banner img, .card_head img {
    display: none;
}
.card_banner {
    background-image: url(http://imgsrc.baidu.com/forum/pic/item/a0a28f3df8dcd10093c4272d748b4710b8122fc3.jpg);
    background-repeat: no-repeat;
    background-size: cover;
    background-position: top right;
}
.card_head a {
    background-image: url(http://imgsrc.baidu.com/forum/pic/item/a14c3df5e0fe99256696535d32a85edf8cb1718e.jpg);
    background-repeat: no-repeat;
    background-size: cover;
    background-position: top right;
    display: block;
    width: 150px;
    height: 150px;
}
}

@-moz-document regexp(".*tieba.baidu.com/(f\\?.*z=|p/).*") {
.card_head a[href="/f?kw=chrome&ie=utf-8"] img {
    display: none;
}
.card_head a[href="/f?kw=chrome&ie=utf-8"] {
    background-image: url(http://imgsrc.baidu.com/forum/pic/item/a0a28f3df8dcd1008516192c748b4710b8122f9e.jpg);
    background-repeat: no-repeat;
    background-size: cover;
    background-position: top right;
    display: block;
    width: 48px;
    height: 48px;
}
}
