css:
@-moz-document url-prefix("http://kjanime.net/foro/index.php?") {
body
{
    background-image:url(http://i542.photobucket.com/albums/gg406/Nyah723/background-Anime-dark_by_nyah.jpg);
    color: white;
    cursor: url(http://cur.cursors-4u.net/cursors/cur-2/cur116.cur), progress !important;
}
#content_section {
    background:none;
}
.table_list tbody.content td.info a.subject {
  font-weight: bold;
  font-size: 110%;
  color: white;
}
td
{
  color: black;
}
a:link, a:visited {
  color: #5f5fff;
  text-decoration: none;
}
a:hover
{ 
  text-decoration: none;
  text-shadow: 0px 3px 20px #ffffff;
  cursor: url(http://cur.cursors-4u.net/cursors/cur-2/cur117.cur), progress !important;
}
.alerts_hide {
  opacity: inherit;
  -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=20)";
}
#header {
  background: none;
}
.windowbg2
{
  color: white;
}
.windowbg
{
  color: white;
}
.description{
  background:none;   
}
.lockedbg2 {
  color: black;
}
.lockedbg2 a{
  color:black;  
  font-weight: bold;
}
.subject stickybg2 p
{
  color: black;   
}
.information
{
  background:none;
}
.username h4
{
  color:white;
}
.counter
{
  color:white;
}
.content h3
{
  color:white;   
}
#footer a.backtop {
  background: url(http://1.bp.blogspot.com/_89SmQSWt-eY/TNxrYDodHSI/AAAAAAAABvY/ZLaGDjoViqA/s1600/arriba.png);
  width: 32px;
  height: 32px;
  display:block;
}
}

