      /* CHAT POPUP BUTTON */
  
.meshim_widget_components_chatButton_Button
{
  background: transparent linear-gradient(to top, #3a93d1, #05609f) repeat scroll 0% 0%; 
}
  
  .meshim_widget_components_chatButton_Button .favicon
{
   background: transparent linear-gradient(to top, #3a93d1, #05609f) repeat scroll 0% 0%; 
}
  
  .meshim_widget_widgets_TitleBar 
{
   Color: #FFFFFF;
   background: transparent linear-gradient(to top, #3a93d1, #05609f) repeat scroll 0% 0%; 
}
  .meshim_widget_widgets_ConnAwareSubmit
{
   Color: #FFFFFF;
   background: transparent linear-gradient(to top, #3a93d1, #05609f) repeat scroll 0% 0%; 
   Border-color: #1d3a8d;
}
  
  .meshim_widget_widgets_ConnAwareSubmit:hover
{
   Color: #FFFFFF;
   background: transparent linear-gradient(#7684a0, #2b416d) repeat scroll 0% 0%;
