css:
@-moz-document domain('kingforums.net'){
html .panel{
    border-bottom: 3px solid #520303 !important;
}
html body{
    background-image: url(http://i.imgur.com/7QPDukn.jpg);
}

html .menu ul{
    background: #4C0000;
    border-top: 1px solid #520303;
}
html .shadetabs li a.selected{
    background: #500404
}
html ::-webkit-scrollbar-thumb{
    background: #460101
}
html .thead{
    background: #4C0000 !important;
    border-top: 1px solid #4C0000!important;
}
html .footer-copyright{
    border-bottom:#4C0000
}
html .menu li > a:hover, .menu ul li:hover{
    background: #4C0000 !important;
}
html .drop-down{
    border-top: 3px solid #4C0000;
}
html input.button{
    background-color: #4C0000 !important;
}
html button:hover{
    background: #4C0000 !important;
}
html a.button:link, a.button:hover, a.button:visited, a.button:active{
    background: #4C0000;
}
html .postbit_buttons > a:link, .postbit_buttons > a:hover, .postbit_buttons > a:visited, .postbit_buttons > a:active{
    background: #4C0000;
}

html html, body, p, code:before, table{
    color: #FFF;
}
}
