css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url('http://www.cnnturk.com/canli-yayin') {
.header, .top-news-wrapper, .must-head, .category-wrapper, .right-col, 
  .tv-programs-wrapper, .footer, 
  .breadcrumb-wrapper, .header-affix, .releated-news, .new-site .carousel, 
    .news-detail-page .main-page-news, #disqus_thread, h2, .keywords, .playlist, ul, 
    .social-wrapper, .right-social-wrapper, .views, .view-date, .text, .sp-fancybox-iframe, .sp-fancybox-wrap, #spEvent, #sspWorker 
.overlay-fixed, .hidden-sm {
display: none !important; 
height: 0 !important; 
width: 0 !important; 
visibility: hidden !important; 
  }
    
*, html, body {
padding:0 !important;  
margin:0 !important;  
position: unset; 
overflow: hidden;
border: none !important; 
z-index: 0; 
   }
    
body {
position: unset !important; 
   }
    
a {
display: none !important; 
height: 0 !important; 
width: 0 !important; 
visibility: hidden !important; 
z-index: -1 !important;  
}
.main-container {
left:0 !important; ;
top:0 !important; 
width: 100% !important;   
padding:0 !important;  
margin:0 !important;  
margin-left:0  !important; 
min-width: 100%  !important; 
}
.container, .col-md-8, .video-page.news-detail-page .news-row.first.big .photo-container {
width: 100% !important;   
}
.canli-yayin .photo .no-padding, .main-container { 
width: 100% !important;   
padding:0 !important;  
margin:0 !important;  
min-width: 100%;
  }
.new-site .container iframe{
display: block !important; 
top: 0;
left: 0;
border: none;         
height: 100vh !important;        
width: 100vw !important; 
max-height: 100%; 
min-height:  100%;
padding:0 !important;  
margin:0 !important; 
z-index:999999 !important; 
}

.container, .new-site .container {
width: 100% !important; width: 100vw !important; 
z-index:999999 !important; 
}
  }
