css:
@-moz-document domain("tomshardware.com") {
    p {
        font-size: 175% !important;
        line-height: 22px !important;
        font-family: sans-serif;
    }
}

@-moz-document url-prefix("file:///") {
    #container .column {
        font-size: 150% !important;
    }
}

@-moz-document domain("techreport.com") {
    p {
        font-size: 125% !important;
    }
}

@-moz-document url-prefix("http://gcc.gnu.org/onlinedocs/") {
    p {
        max-width: 60% !important;
        margin-left: 15% !important;
    }
}

@-moz-document url-prefix("http://vimdoc.sourceforge.net/htmldoc/") {
    pre {
        font-size: 140%;
        margin-left: 15%;
    }
}

@-moz-document url-prefix("http://cvs.so.sohu.com:2080/svn/") {
    .listing {
        font-size: 140%;
        margin-left: 10%;
        margin-right: 10%;
    }
}

@-moz-document url-prefix("http://hg101.classicgaming.gamespy.com/") {
    body {
        background-color : #FFFFFF !important;
    }
    font {
        font-family: sans-serif !important;
        color: black !important;
    }
    a:link {
        color: blue !important;
    }
}
