css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("web.whatsapp.com") {
    /** Body's properties */
    body, .app-wrapper::before, .app-wrapper, .pane-intro {
      background: #333 url('http://htmlandcssbook.com/code-samples/chapter-17/images/dark-wood.jpg') repeat fixed !important;
    }
    /** Intro Page's properties */
    .pane-intro {
      color: #999 !important;
    }
    /** Sidebar Header's properties */
    .pane-header, .pane-subheader, .cont-input-search, .input-search, .pane-footer, .infinite-list-item {
      background: #555 !important;
      color: #ccc !important;
      border-color: #555 !important; /** So not visible border */
    }
    /** Sidebar text's properties */
    .chat-title, .chat-status {
      color: #ccc !important;
    }
    /** Sidebar History */
    .chat, .chat-body {
      background: #555 !important;
      color: #ccc !important;
    }
    .pane-chat {
      background: #333 !important;
    }
    /**  General message's properties */
    .message {
      color: #ccc !important;
    }
    /** Message's properties */
    .message-system-body, .bubble, .message-in .bubble {
      background: #666 !important;
      color: #ccc !important;
      border: 0 !important;
      border-radius: 1% !important;
    }
    .message-text {
      color: #ccc !important;
    }
    /** Inbox Message's properties */
    .message-in .bubble {
      background: #555 !important;
    }
    /** Delete bubble object */
    .message-out::before, .message-in::after, .message-in::before {
      display: none !important;
    }
    /** Input Message's properties */
    .input-container {
      background: #444 !important;
      color: #ccc !important;
    }
}
