html {
	height: 100%;
}
body {
	height: 100%;
    overflow: hidden;
}
#mainframe {
	margin: 4px 4px !important;
    width: auto !important;
    height: calc(100% - 8px) !important;
}
#shoutbox, .windowbg2, .content {
	height: 100%;
}
