css:
@-moz-document domain("docs.ruby-lang.org") {
body {
  max-width: 1000px;
  margin: 20px auto 200px;
}

pre {
  font-size: 16px;
  line-height: 1.3;
}

h2 {
  margin-top: 2.5em;
  background-color: #E0E0F2;
  padding: 0.25em 0.5em;
}

h3 {
  margin-top: 1.5em;
}

.method-heading {
  font-size: 18px;
  padding: 4px;
  border-bottom: 2px solid #E0E0F2;
}
.method-heading[id] {
  margin-top: 2.5em;
}

}
