css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linux.org.ru") {

html, body, .code, code {

    background: #454545 !important;

    color: #c1c1c1 !important;

}

    #bd a.tag {

    color: #c1c1c1;

}

    .message-table .tag {

     background: #3a3a3a;

      
}

    .boxlet {

    background-color: rgba(69, 69, 69, 0) !important;

    border: 1px solid #3a3a3a;

    border-radius:  0;

}
  
      a {

    color: #c1c1c1 !important;

}

     a.visited {

    color: #c1c1c1 !important;

}

    .infoblock{

     background-color: rgba(20, 20, 20, 0.03) !important;
}

    .messages .msg {

    background: none repeat scroll 0% 0% rgba(20, 20, 20, 0.03);
}

    .nav-buttons a {

    color: #3a3a3a !important;
}

    #bd .forum table th, .message-table tr:hover {

    background: #3a3a3a !important;
}

    #sitetitle {

    color: #c1c1c1 !important;

}

    h2 a {

    color: #f2f2f2 !important;

}

    .menu a {

    color: #c1c1c1 !important;

    text-decoration: none;

}

   #loginGreating a {

    color: #c1c1c1 !important;

    text-decoration: none;
}

    #bd .btn-selected {
   
    background-color: #3b3b3b;
      
    color: #c1c1c1;      
}
  
    #bd .btn-default {
      
    background-color: #404040;
}

    #bd .forum table tbody td {

    border: 1px solid #343430;      
}

    #whois_userpic {

    background: none repeat scroll 0% 0% #424242
}


    #bd .btn-primary {
    background-color: #2893b5;
    color: #c1c1c1;
}


    #bd .btn-danger {
    background-color: #2893b5;
    color: #c1c1c1;  
}  
  
    img {

    filter: contrast(100%) brightness(85%);   
}     
      
    .stars {

    color: #31b5de;      
}
 
   .group-label {
    background-color: #3a3a3a;
}

    .fav-buttons .selected [class^="icon-"] {
    color: #31b5de;
}    

    .messages .msg:target {
    border: 1px solid #31b5de;
}
  
    #bd h1, #bd h2 {
      
    color: #f2f2f2;
}
    
    .message-table a.secondary {
        
     color: #f2f2f2 !important;
}
  
    #bd .btn-default {
      
    background-color: #6b6b6b;
}
    
    .user-remark {
    color: #31b5de;
}
}
