css:
@-moz-document domain(4chan.org) {
  body, html, #ft li.fill, .yui-panel .bd, hr {background:rgb(22,22,22)!important; color:rgb(150,150,150);}
  div.post.reply, .reply, .boxcontent, .box-outer, div.reply, div#quote-preview, #entries tr, #masthead, #content article.post, #content article.post .post-meta, #sidebar, .button.load-more {background:rgb(15,15,15); border-color:rgb(20,20,20)!important;}
  #postForm > tbody > tr > td, input[type="text"], input[type="text"]:focus, textarea, textarea:focus, #ft li, footer li, #disclaimer-dialog .hd, .yui-simple-dialog .ft, .post.reply.highlight, .reply:target, .postblock, div.pagelist, .dd-menu ul {background:rgb(30,30,30)!important; color:rgb(150,150,150)!important; border-color:rgb(20,20,20)!important;}
  .boxbar, .dd-menu li:hover, #entries th, .content h3 {background:rgb(35,35,35)!important;}
  hr {border-color:rgb(50,50,50)!important;}
  div.boardBanner, .fileThumb > img, div#logo {opacity: 0.8}
  span.postNum.desktop > a, .boxcontent, div#absbot, div#quote-preview, #entries tr, #entries th, .content, #sidebar h5, .button.load-more {color:rgb(150,150,150)!important;}
  #announce.box-outer {padding-bottom:0em!important;}
  #ft ul, footer {border-top:0px}
  .dd-menu li, .boxcontent dd {border-bottom: 1px solid rgb(20,20,20);}
  .mask {background-color:#565454;}
  a, .quotelink, a.replylink:not(:hover), .yotsuba_b_new .backlink a, div#absbot a:not(:hover), .subject, dt {color: #D00 !important;}
  a:hover, div.post div.postInfo span.postNum a:hover, .yotsuba_b_new .backlink a:hover {color: #F00 !important;}
}
