css:
@-moz-document domain("play.google.com") {
.material-card[data-is-radio="true"],
.material-card[data-is-listen-now="true"],
.material-card[data-type="imfl"],
.nav-item-container[data-type="wbs"],
.nav-item-container[data-type="sub"],
.material-cluster[data-type="situations"],
[data-type="wms"],
.goog-menuitem[id=":1e"],
.goog-menuitem[id=":1"],
.goog-menuitem[id=":p"],
.situations-container,
.ups,
.recommended-header {
    display: none !important;
}
.new-listen-now::after {
    content: "All radio features have been hidden from view.  Disable 'Play Music - Disable Radio' to use Radio features.";
    color: lightgrey;
}
}
