css:
@-moz-document url-prefix("https://netrunnerdb.com/") {
a {
    color: rgb(162, 212, 253);
}
body {
    background-color: rgb(29, 29, 29);
    color: cyan;
}
#wrapper,
.panel,
.jumbotron {
    background: rgb(29, 29, 29);
}
code {
    color: #FFB268;
    background: rgb(29, 29, 29);
    background-color: rgb(29, 29, 29)
}
.panel-title,
.dropdown-menu>li>a {
    color: cyan;
}
.panel-default>.panel-heading,
#navbar,
.dropdown-menu,
.pagination a,
.pagination li a,
.pagination ul a,
ul.pagination,
.pagination li.disabled a,
#comment-form-preview,
.alert-warning,
.pager li>a,
.pager li>span,
.qtip-bootstrap,
.modal-content,
pagination>li>a,
.pagination>li>span,
.well,
.tt-dropdown-menu {
    background-color: rgb(63, 99, 115);
    color: cyan;
}
legend {
    color: rgb(127, 0, 136);
    border-bottom: 1px solid purple;
}
footer {
    color: cyan !important;
    padding: 10px 0;
    background-color: rgb(63, 99, 115) !important;
}
#navbar-factions {
    background-color: rgb(37, 37, 69);
}
div#decks {
    background-color: rgb(63, 99, 115) !important;
    color: cyan !important;
}
.h1,
.h2,
.h3,
.h4,
.h5,
.h6,
h1,
h2,
h3,
h4,
h5,
h6 {
    color: Cyan;
}
.influence-haas-bioroid,
.icon-haas-bioroid,
.haas-bioroid {
    color: rgb(255, 0, 223) !important;
    border-color: rgb(255, 0, 223) !important;
}
.influence-anarch,
.icon-anarch,
.anarch,
.icon-bg.icon-anarch:before {
    color: red !important;
    border-color: red !important;
}
.influence-shaper,
.icon-shaper,
.shaper,
.icon-bg.icon-shaper:before {
    color: limegreen !important;
    border-color: limegreen !important;
}
.influence-jinteki,
.icon-jinteki,
.jinteki,
.icon-bg.icon-jinteki:before {
    color: red !important;
    border-color: red !important;
}
.influence-nbn,
.icon-nbn,
.nbn,
.icon-bg.icon-nbn:before {
    color: orange !important;
    border-color: orange !important;
}
.influence-weyland-consortium,
.icon-weyland-consortium,
.weyland-consortium,
.icon-bg.icon-weyland-consortium:before {
    color: rgb(178, 178, 178) !important;
    border-color: rgb(178, 178, 178) !important;
}
.influence-criminal,
.icon-criminal,
.criminal,
.icon-bg.icon-criminal:before {
    color: rgb(166, 198, 255) !important;
    border-color: rgb(166, 198, 255) !important;
}
ul#navbar-factions.nav.nav-pills.nav-justified.hidden-xs li:hover {
    color: cyan;
}
div.ad-blocked {
    background: rgb(63, 99, 115);
    padding: 0;
    border-color: purple cyan cyan purple;
}
.btn-default,
.list-group-item,
.form-control,
.pagination {
    background: rgb(63, 99, 115);
    color: cyan;
}
a.list-group-item {
    color: rgb(3, 199, 199);
}
.btn-default.active,
.btn-default.focus,
.btn-default:active,
.btn-default:focus,
.btn-default:hover,
.open>.dropdown-toggle.btn-default,
.active,
.selected,
.deck-list-group-item.selected,
.deck-list-group-item:hover,
.pagination:hover,
.pagination li a:hover,
.pagination a:hover,
.pagination ul a:hover,
.pagination li.active.a,
.pagination>.active>a,
.pagination>.active>a:focus,
.pagination>.active>a:hover,
.pagination>.active>span,
.pagination>.active>span:focus,
.pagination>.active>span:hover,
.pager li>a:hover,
.pager li>span:hover {
    background: rgb(29, 29, 29) !important;
    color: cyan !important;
}
.btn-default.disabled,
.btn-default.disabled.active,
.btn-default.disabled.focus,
.btn-default.disabled:active,
.btn-default.disabled:focus,
.btn-default.disabled:hover,
.btn-default[disabled],
.btn-default[disabled].active,
.btn-default[disabled].focus,
.btn-default[disabled]:active,
.btn-default[disabled]:focus,
.btn-default[disabled]:hover,
fieldset[disabled] .btn-default,
fieldset[disabled] .btn-default.active,
fieldset[disabled] .btn-default.focus,
fieldset[disabled] .btn-default:active,
fieldset[disabled] .btn-default:focus,
fieldset[disabled] .btn-default:hover {
    color: cyan;
    background: rgb(29, 29, 29);
}
.nav-pills li a:hover,
.dropdown-menu li a:hover,
.navbar-factions li a:hover {
    background: #337ab7 !important;
    color: cyan !important;
}
#recent_decklists,
#recent_reviews {
    border-left: 1px solid purple;
}
#index_page article.review,
.table>tbody>tr>td,
.table>tbody>tr>th,
.table>tfoot>tr>td,
.table>tfoot>tr>th,
.table>thead>tr>td,
.table>thead>tr>th {
    border-top: 1px solid purple;
}
.table-striped>tbody>tr:nth-of-type(odd),
.tt-menu {
    background: rgb(29, 29, 29);
}
.pack-future,
.card-preview {
    color: blueviolet;
}
.close {
    color: yellow;
}
}
