css:
@-moz-document url-prefix("https://cookidoo.de/vorwerkWebapp/") {
@media print {
	#private-shopping-list nav{
		display:none;
	}


	#private-shopping-list .shopping-list-recipes, #private-shopping-list .shopping-header, #private-shopping-list .shopping-info-header, #private-shopping-list .shopping-checked-items, #private-shopping-list .shopping-add, #private-shopping-list div.shopping-options, #private-shopping-list .shopping-ingredients .checkmark, #private-shopping-list .weekly-planner, #private-shopping-list .icon-edit-simple{
		display:none;
	}

	#private-shopping-list .shopping-list-group{
		display:block;
	}
    
	#private-shopping-list .shopping-content-box{
		padding:0px;
		box-shadow:none;
		margin-bottom:5px;
		width: 49%;
		float: left;
	}

    #private-shopping-list .shopping-content-box.clearfix{
    	clear:none;                                    
	}
    
    #private-shopping-list .shopping-content-box h3{
    	margin:0px;                                    
    }
    
    #private-shopping-list .shopping-ingredients div.inner-container{
    	padding:0px;
		display:inline;
	}

    #private-shopping-list .shopping-ingredients div.inner-container label{
    	margin:0px;                                    
	}

	#private-shopping-list .shopping-ingredients-list .list-item{
		border-bottom:0px;
	}


	#private-shopping-list .shopping-content-collapsable{
		padding-bottom:0px;
	}

	#private-shopping-list{
		background-image:none;
	}
    
    #private-shopping-list li.list-item-todo-animate:before
    {
    	content: "\2610";
		margin-right:6px;
    }
}
}
