css:
@-moz-document domain('hackerrank.com') {
  .select2-highlighted, .select2-choice, .select2-container, #s2id_select-lang>a {
      color:blue !important;
      background-color:rgb(40, 40, 40) !important;
  }
  .CodeMirror-hint-infoDiv li, 
  .CodeMirror-hints li, 
  .CodeMirror-hint-divCompletionText {
      background-color:rgba(0, 0, 0, 0.7) !important;
      color:white !important;
  }
  .CodeMirror-hint-divTypeText,
  #CodeMirror-hint-infoDiv, #CodeMirror-hint-infoDivMenu, #CodeMirror-hint-infoDivInfo
  {
      background-color:rgba(0, 0, 0, 0.7) !important;
      color:rgba(0, 100, 255, 1) !important;
  }
  .CodeMirror-hint-divMenuText
  {
      background-color:rgba(0, 40, 100, 0.8) !important;
      color:white !important;
  }

  .CodeMirror-activeline-background
  {
      background-color: rgba(0, 85, 165, .5)!important;
  }
  .CodeMirror-cursor {
      border-left-color: white !important;
  }

  .CodeMirror-selected{
      background-color: rgba(50, 150, 255, 1)!important;
  }

  * {
      background-color:rgba(0,0,0,0.3) !important;
      color:rgb(190, 190, 190);

  }
  pre {
      color:Green !important;
  }
  .cm-keyword {
      color:rgb(60, 180, 255)!important;
  }
  .cm-builtin {
      color:rgb(0, 100, 255) !important;
  }
  .cm-variable {
      color:rgb(190, 190, 190)!important;
  }
  .cm-operator {
      color:gray !important;
  }
  .cm-comment {
      color: green !important;
  }
  .cm-number {
      color:orangered  !important;
  }
  .cm-string
  {
      color: orchid !important;
  }
  .error-marker {
      background-color:rgb(255, 100, 100) !important;
  }
}
