css:
@-moz-document domain("slack.com") {


body {
    background: #151515;
    color: #FFFFFF;
}

#header,
#channel_members_toggle, #footer {
    background: #2D2C2C;
}

#active_channel_name .name,
.light_theme .message_sender {
    color: #FFFFFF;
}

.search_form .search_input {
    border-radius: 0px;
    border: 0px;
}

.light_theme .message {
    color: #E4E3E6;
}

.monkey_scroll_bar {
    width: 8px;
    background: #2D2C2C;
    border-radius: 0px;
}

#client-ui .monkey_scroll_handle_inner {
    border: 0px;
    background: #5A5959;
    border-radius: 0px;
}
}
