css:
@-moz-document url("http://blog.bitcoin-mining.com/vip-html5-player/"), url-prefix("http://fpgaminer.github.io/") {
#tracks-table td, .credits a, #tracks-table div{
	color:cyan !important;
	background:rgb(29,29,29);
}

#tracks-table tr.selected td, #tracks-table div.selected{
    background-color: rgb(63, 99, 115) !important;
    color: cyan !important;
}

.control-buttons td, .control-playlist select, .control-buttons, table.control-buttons{
	color:limegreen;
    background:linear-gradient(to bottom, rgba(0,0,0) 0%, rgba(29,29,29) 100%) !important;
}

#scrubber-progress{
	background:cyan;   
}

#scrubber-track{
	background:teal;
}

#scrubber{
    background:blueviolet;
    border:black 1px solid;
}

::-webkit-scrollbar-track{
	background:teal;   
}

::-webkit-scrollbar-thumb{
	background:cyan !important;   
}

#tracks-table td:hover{
	color:cyan;   
}
}
