css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.gamestar.de/community/gspinboard/index.php"), url("http://www.gamestar.de/community/gspinboard/") {
td.tcat[colspan] > div {
  height: 20px !important;
  overflow: hidden !important;
  padding-top: 0 !important;
  padding-bottom: 0 !important;
  float: right !important;
}

td.tcat[colspan] > div:first-child {
  padding-right: 20px;
}

td.tcat[colspan] > div:last-child {
  padding-right: 17px;
}

td.tcat[colspan] {
  height: 20px !important;
  padding: 0 !important;
}

td.tcat[colspan] img {
 padding-top: 3px;
}
}
