css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.gamestar.de/community/gspinboard/forumdisplay.php") {
table#threadslist > tbody:last-child > tr > td:nth-child(1) {
  max-width: 20px;
  overflow: hidden;
}

table#threadslist > tbody:last-child > tr > td:nth-child(2) {
  max-width: 16px;
  overflow: hidden;
}

table#threadslist > tbody:last-child > tr > td:nth-child(4) > div > a {
  display: inline-block;
  max-width: 92px;
  overflow: hidden;
  vertical-align: text-top;
}
}
