css:
@-moz-document domain("behance.net") {
  #user-avatar,
  #profile-image,
  .profile-list-image-wrap,
  .info img,
  .timeline-container .avatar .avatar-image,
  .user-row .user-image-wrap,
  .mini-profile-wrap .user-image-wrap,
  .multiple-owners-list .tooltipi .multiple-owner-image-container,
  .project-cover-minimal .user-image,
  .wip-comment-user-link,
  .wip-comment-user-img-el {
    display: none !important;
  }
  .info {
    padding-left: 0;
  }
  .profile-list-info {
    margin-left: 0;
  }
  #profile-actions {
    position: relative;
    bottom: auto;
    left: auto;
  }
  .project-cover-minimal .user-info,
  .wip-comment-text {
    float: none;
    width: 100%;
  }
}
