css:
@-moz-document domain("deviantart.com") {
  body.gruze .catbar img.avatar,
  #aboutme-profile-pic,
  img.avatar,
  i.l,
  body .cc-avatar,
  .dev-page-view .dev-title-container .dev-title-avatar {
    display: none !important;
  }
  .ccomment-form {
    margin-left: 0;
  }
  .ccomment a.reply {
    bottom: 6px !important;
    left: 6px !important;
  }
  .ccomment .cc-in {
    min-height: 26px;
    padding: 8px 8px 36px 8px;
  }
}
