css:
@-moz-document domain("dribbble.com") {
  div.profile img.photo,
  div.profile-head h1 img.photo,
  img.photo,
  div.secondary ol.jobs-list li a.item-team img,
  div.secondary ol.jobs-list li span.item-no-team img,
  #footer ol.jobs-list li a.item-team img,
  #footer ol.jobs-list li span.item-no-team img,
  div.city-side ol.jobs-list li a.item-team img,
  div.city-side ol.jobs-list li span.item-no-team img,
  ol.jobs-list li a.item-team img,
  ol.jobs-list li span.item-no-team img {
    display: none !important;
  }
  div.single-title h1,
  div.single-title h2,
  div.single-title div.shot-byline {
    margin-left: 0 !important;
  }
  h4#pixels-total {
    background: transparent !important;
    padding-left: 0 !important;
  }
  #nav li#t-profile img {
    width: 0 !important;
    height: 0 !important;
    border: 10px solid #ddd !important;
  }
}
