css:
@-moz-document domain("twitter.com") {
  .DashboardProfileCard-bg,
  .DashboardProfileCard-avatarLink,
  .ProfileCanopy-headerBg,
  .ProfileAvatar-image,
  .ProfileAvatar,
  .ProfileCardMini-avatar,
  .account-summary .avatar,
  .ProfileCard-avatarLink,
  .ProfileListItem-avatar {
    display: none !important;
  }
  .avatar {
    visibility: hidden !important;
  }
  .top-timeline-tweetbox .timeline-tweet-box .t1-form,
  .tweet > .context,
  .stream-item .content,
  .permalink-tweet .content,
  .account-summary .content,
  .ProfileCardMini-details {
    margin-left: 0 !important;
  }
  .ProfileHeaderCard {
    margin-top: 21px !important;
  }
  .account, .tweet, .app {
    padding: 27px 36px !important;
  }
  .ProfileCanopy--large,
  .ProfileCanopy--large .ProfileCanopy-header {
    height: auto !important;
    min-height: 18px;
  }
  .ProfileCard-actions {
    top: -50px !important;
  }
}
