css:
@document domain(slack.com) {

.light_theme .message {
    padding-right: 3rem;
    font-family: tahoma;
}
.msgs_holder {
    direction: rtl;
}
.light_theme .message.show_user .timestamp, .light_theme .selecting_messages .message.show_user .timestamp {
    position: initial;
    display: inline-block;
}
.member_image {
    left: initial;
    right: 0px;
}
.inline_attachment .content {
    padding: 0 12px 0 0;
}
.message .edited {
    margin-right: 8px;
}
.inline_attachment .attachment_author_icon, .inline_attachment .attachment_service_icon {
    float: right;
}
#message-input {
    direction: rtl;
}
pre.special_formatting {
    direction: ltr;
    font-size: 13px;
    font-weight: bold;
}
.snippet_container {
    direction: ltr;
    float: left;
    width: 100%;
}
#message_edit_form #message-input,
#messages-input-container #message-input,
.inline_message_input_container #message-input {
	font-family: tahoma;
}

}
